            })
    }

    render() {
        const { onRouteChange, loadUser } = this.props;
        return (
            <article className="br2 ba b--black-10 mv4 w-100 w-50-m w-25-l mw10 shadow-5 center">
                <main className="pa4 black-80">
                    <div className="measure">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0">Sign In</legend>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                                <input 
                                    className="pa2 input-reset ba bg-transparent hover-bg-near-black hover-white w-100" type="email" name="email-address"  id="email-address" 
                                    onChange={this.onEmailChange}
                                />
                            </div>
                            <div className="mv3">
                                <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                                <input 
                                    className="b pa2 input-reset ba bg-transparent hover-bg-near-black hover-white w-100" type="password" name="password"  id="password" 
                                    onChange={this.onPasswordChange}
                                />
                            </div>
                        </fieldset>
                        <div className="">
                            <input 
                                className="b ph3 pv2 input-reset ba b--black bg-transparent hover-bg-light-silver grow pointer f6 dib" 
                                type="submit" 
                                value="Sign In" 
                                onClick={this.onSignInSubmit} 
                            />
                        </div>
                        <div className="lh-copy mt3">
                            <p 
                                onClick={() => onRouteChange('register')} 
                                className="f6 link black db pointer grow"
                            >
                                Register
                            </p>
                        </div>
                    </div>
                </main>
            </article>
        )
    }
}

export default SignIn;
